DROP TABLE CHANNEL_SETTING;
CREATE TABLE CHANNEL_SETTING (
	CHANNEL				TEXT NOT NULL,
	ADDRESS				TEXT NOT NULL,
	MIN_VOLTAGE			NUMERIC NOT NULL,
	MAX_VOLTAGE			NUMERIC NOT NULL,
	GRADIENT_VOLTAGE	NUMERIC NOT NULL,
	INTERCEPT_VOLTAGE	NUMERIC NOT NULL,
	MIN_VALUE			NUMERIC NOT NULL,
	MAX_VALUE			NUMERIC NOT NULL,
	GRADIENT_VALUE		NUMERIC NOT NULL,
	INTERCEPT_VALUE		NUMERIC NOT NULL,
	DESC				TEXT,
	DISPLAY_INDEX		INTEGER,
	CREATE_DT			TIMESTAMP default (datetime('now', 'localtime')) NOT NULL,
	UPDATE_DT			TIMESTAMP default (datetime('now', 'localtime')) NOT NULL,
	PRIMARY KEY(CHANNEL)
);

DELETE FROM CHANNEL_SETTING;






